import React, { useState, useEffect } from 'react';
import {
  Box,
  Container,
  Grid,
  Card,
  CardContent,
  Typography,
  List,
  ListItem,
  ListItemAvatar,
  ListItemText,
  Avatar,
  TextField,
  Button,
  IconButton,
  Divider,
  Badge,
  Alert,
  Chip,
} from '@mui/material';
import {
  Send,
  AttachFile,
  MoreVert,
  Person,
  Business,
  Home,
} from '@mui/icons-material';
import { motion } from 'framer-motion';
import { useAuth } from '../contexts/AuthContext';
import { useSearchParams } from 'react-router-dom';

interface Message {
  id: number;
  content: string;
  sender_id: number;
  receiver_id: number;
  property_id?: number;
  created_at: string;
  is_read: boolean;
}

interface Conversation {
  id: number;
  participant: {
    id: number;
    username: string;
    first_name?: string;
    last_name?: string;
    user_role: string;
    company_name?: string;
    avatar?: string;
  };
  property?: {
    id: number;
    title: string;
    price: number;
    location: string;
  };
  last_message: Message;
  unread_count: number;
}

const MessagesPage: React.FC = () => {
  const { user } = useAuth();
  const [searchParams] = useSearchParams();
  const [conversations, setConversations] = useState<Conversation[]>([]);
  const [selectedConversation, setSelectedConversation] = useState<Conversation | null>(null);
  const [messages, setMessages] = useState<Message[]>([]);
  const [newMessage, setNewMessage] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchConversations();

    // Check if we need to start a new conversation
    const agentId = searchParams.get('agent');
    const propertyId = searchParams.get('property');
    if (agentId && propertyId) {
      // TODO: Start new conversation with agent about property
    }
  }, [searchParams]);

  const fetchConversations = async () => {
    try {
      setLoading(true);
      // TODO: Replace with actual API call
      // Mock data for now
      const mockConversations: Conversation[] = [
        {
          id: 1,
          participant: {
            id: 2,
            username: 'sarah_agent',
            first_name: 'Sarah',
            last_name: 'Johnson',
            user_role: 'seller_agent',
            company_name: 'Premier Realty',
            avatar: '/api/placeholder/40/40'
          },
          property: {
            id: 1,
            title: 'Beautiful 3BR Home in Naperville',
            price: 425000,
            location: 'Naperville, IL'
          },
          last_message: {
            id: 1,
            content: 'Hi! I saw your interest in the Naperville property. Would you like to schedule a viewing?',
            sender_id: 2,
            receiver_id: user?.id || 1,
            property_id: 1,
            created_at: '2024-01-15T14:30:00Z',
            is_read: false
          },
          unread_count: 1
        },
        {
          id: 2,
          participant: {
            id: 3,
            username: 'mike_agent',
            first_name: 'Mike',
            last_name: 'Davis',
            user_role: 'buyer_agent',
            company_name: 'Urban Properties',
            avatar: '/api/placeholder/40/40'
          },
          last_message: {
            id: 2,
            content: 'Thanks for the property details. My client is very interested.',
            sender_id: user?.id || 1,
            receiver_id: 3,
            created_at: '2024-01-14T16:45:00Z',
            is_read: true
          },
          unread_count: 0
        }
      ];

      setConversations(mockConversations);
      if (mockConversations.length > 0) {
        setSelectedConversation(mockConversations[0]);
        fetchMessages(mockConversations[0].id);
      }
    } catch (err: any) {
      setError('Failed to load conversations');
    } finally {
      setLoading(false);
    }
  };

  const fetchMessages = async (conversationId: number) => {
    try {
      // TODO: Replace with actual API call
      // Mock messages for now
      const mockMessages: Message[] = [
        {
          id: 1,
          content: 'Hi! I saw your interest in the Naperville property. Would you like to schedule a viewing?',
          sender_id: 2,
          receiver_id: user?.id || 1,
          property_id: 1,
          created_at: '2024-01-15T14:30:00Z',
          is_read: false
        },
        {
          id: 2,
          content: 'Yes, I\'m very interested! When would be a good time?',
          sender_id: user?.id || 1,
          receiver_id: 2,
          property_id: 1,
          created_at: '2024-01-15T14:35:00Z',
          is_read: true
        },
        {
          id: 3,
          content: 'How about this Saturday at 2 PM? I can meet you at the property.',
          sender_id: 2,
          receiver_id: user?.id || 1,
          property_id: 1,
          created_at: '2024-01-15T14:40:00Z',
          is_read: false
        }
      ];

      setMessages(mockMessages);
    } catch (err: any) {
      setError('Failed to load messages');
    }
  };

  const handleSendMessage = async () => {
    if (!newMessage.trim() || !selectedConversation) return;

    try {
      // TODO: API call to send message
      const newMsg: Message = {
        id: Date.now(),
        content: newMessage,
        sender_id: user?.id || 1,
        receiver_id: selectedConversation.participant.id,
        property_id: selectedConversation.property?.id,
        created_at: new Date().toISOString(),
        is_read: false
      };

      setMessages(prev => [...prev, newMsg]);
      setNewMessage('');
    } catch (err: any) {
      setError('Failed to send message');
    }
  };

  const formatTime = (dateString: string) => {
    return new Date(dateString).toLocaleTimeString('en-US', {
      hour: 'numeric',
      minute: '2-digit',
      hour12: true
    });
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      month: 'short',
      day: 'numeric'
    });
  };

  const getRoleColor = (role: string) => {
    switch (role) {
      case 'buyer_agent':
        return 'primary';
      case 'seller_agent':
        return 'secondary';
      case 'buyer':
        return 'info';
      case 'seller':
        return 'warning';
      default:
        return 'default';
    }
  };

  const getRoleLabel = (role: string) => {
    switch (role) {
      case 'buyer_agent':
        return 'Buyer Agent';
      case 'seller_agent':
        return 'Seller Agent';
      case 'buyer':
        return 'Buyer';
      case 'seller':
        return 'Seller';
      default:
        return role;
    }
  };

  if (loading) {
    return (
      <Container maxWidth="xl" sx={{ py: 4 }}>
        <Typography>Loading messages...</Typography>
      </Container>
    );
  }

  return (
    <Container maxWidth="xl" sx={{ py: 4 }}>
      {/* Header */}
      <Box sx={{ mb: 4 }}>
        <Typography variant="h4" component="h1" gutterBottom sx={{ fontWeight: 'bold' }}>
          Messages
        </Typography>
        <Typography variant="body1" color="text.secondary">
          Communicate with agents and clients
        </Typography>
      </Box>

      {/* Error Alert */}
      {error && (
        <Alert severity="error" sx={{ mb: 3 }} onClose={() => setError('')}>
          {error}
        </Alert>
      )}

      {/* Role-based Communication Rules */}
      {user && (
        <Alert severity="info" sx={{ mb: 3 }}>
          {user.user_role === 'buyer' && 
            'As a buyer, you can contact seller agents directly. For other inquiries, work with your buyer agent.'}
          {user.user_role === 'seller' && 
            'As a seller, you can contact buyer agents through your seller agent. Direct communication helps streamline the process.'}
          {user.user_role === 'buyer_agent' && 
            'As a buyer agent, you can communicate with sellers and seller agents on behalf of your clients.'}
          {user.user_role === 'seller_agent' && 
            'As a seller agent, you can communicate with buyers and buyer agents on behalf of your clients.'}
        </Alert>
      )}

      <Grid container spacing={3} sx={{ height: 'calc(100vh - 300px)' }}>
        {/* Conversations List */}
        <Grid item xs={12} md={4}>
          <Card sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
            <CardContent sx={{ pb: 0 }}>
              <Typography variant="h6" gutterBottom>
                Conversations
              </Typography>
            </CardContent>
            <List sx={{ flexGrow: 1, overflow: 'auto' }}>
              {conversations.map((conversation) => (
                <ListItem
                  key={conversation.id}
                  button
                  selected={selectedConversation?.id === conversation.id}
                  onClick={() => {
                    setSelectedConversation(conversation);
                    fetchMessages(conversation.id);
                  }}
                  sx={{
                    borderRadius: 1,
                    mx: 1,
                    mb: 1,
                    '&.Mui-selected': {
                      backgroundColor: 'primary.light',
                      '&:hover': {
                        backgroundColor: 'primary.light',
                      },
                    },
                  }}
                >
                  <ListItemAvatar>
                    <Badge badgeContent={conversation.unread_count} color="error">
                      <Avatar src={conversation.participant.avatar}>
                        {conversation.participant.first_name?.charAt(0) || 
                         conversation.participant.username.charAt(0).toUpperCase()}
                      </Avatar>
                    </Badge>
                  </ListItemAvatar>
                  <ListItemText
                    primary={
                      <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                        <Typography variant="subtitle2">
                          {conversation.participant.first_name} {conversation.participant.last_name}
                        </Typography>
                        <Chip
                          size="small"
                          label={getRoleLabel(conversation.participant.user_role)}
                          color={getRoleColor(conversation.participant.user_role)}
                          variant="outlined"
                        />
                      </Box>
                    }
                    secondary={
                      <Box>
                        {conversation.participant.company_name && (
                          <Typography variant="caption" color="text.secondary" display="block">
                            {conversation.participant.company_name}
                          </Typography>
                        )}
                        {conversation.property && (
                          <Box sx={{ display: 'flex', alignItems: 'center', gap: 0.5, mt: 0.5 }}>
                            <Home fontSize="small" color="action" />
                            <Typography variant="caption" color="text.secondary">
                              {conversation.property.title}
                            </Typography>
                          </Box>
                        )}
                        <Typography variant="body2" sx={{ mt: 0.5 }} noWrap>
                          {conversation.last_message.content}
                        </Typography>
                        <Typography variant="caption" color="text.secondary">
                          {formatDate(conversation.last_message.created_at)}
                        </Typography>
                      </Box>
                    }
                  />
                </ListItem>
              ))}
            </List>
          </Card>
        </Grid>

        {/* Messages Area */}
        <Grid item xs={12} md={8}>
          {selectedConversation ? (
            <Card sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
              {/* Chat Header */}
              <CardContent sx={{ pb: 1, borderBottom: 1, borderColor: 'divider' }}>
                <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
                  <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
                    <Avatar src={selectedConversation.participant.avatar}>
                      {selectedConversation.participant.first_name?.charAt(0) || 
                       selectedConversation.participant.username.charAt(0).toUpperCase()}
                    </Avatar>
                    <Box>
                      <Typography variant="h6">
                        {selectedConversation.participant.first_name} {selectedConversation.participant.last_name}
                      </Typography>
                      <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                        <Chip
                          size="small"
                          label={getRoleLabel(selectedConversation.participant.user_role)}
                          color={getRoleColor(selectedConversation.participant.user_role)}
                          variant="outlined"
                        />
                        {selectedConversation.participant.company_name && (
                          <Typography variant="body2" color="text.secondary">
                            • {selectedConversation.participant.company_name}
                          </Typography>
                        )}
                      </Box>
                    </Box>
                  </Box>
                  <IconButton>
                    <MoreVert />
                  </IconButton>
                </Box>

                {/* Property Info */}
                {selectedConversation.property && (
                  <Box sx={{ mt: 2, p: 2, backgroundColor: 'grey.50', borderRadius: 1 }}>
                    <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                      <Home color="primary" />
                      <Typography variant="subtitle2">
                        {selectedConversation.property.title}
                      </Typography>
                    </Box>
                    <Typography variant="body2" color="text.secondary">
                      ${selectedConversation.property.price.toLocaleString()} • {selectedConversation.property.location}
                    </Typography>
                  </Box>
                )}
              </CardContent>

              {/* Messages List */}
              <Box sx={{ flexGrow: 1, overflow: 'auto', p: 2 }}>
                {messages.map((message) => (
                  <motion.div
                    key={message.id}
                    initial={{ opacity: 0, y: 10 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ duration: 0.2 }}
                  >
                    <Box
                      sx={{
                        display: 'flex',
                        justifyContent: message.sender_id === user?.id ? 'flex-end' : 'flex-start',
                        mb: 2,
                      }}
                    >
                      <Box
                        sx={{
                          maxWidth: '70%',
                          p: 2,
                          borderRadius: 2,
                          backgroundColor: message.sender_id === user?.id ? 'primary.main' : 'grey.100',
                          color: message.sender_id === user?.id ? 'white' : 'text.primary',
                        }}
                      >
                        <Typography variant="body1">
                          {message.content}
                        </Typography>
                        <Typography
                          variant="caption"
                          sx={{
                            display: 'block',
                            mt: 0.5,
                            opacity: 0.8,
                            textAlign: 'right',
                          }}
                        >
                          {formatTime(message.created_at)}
                        </Typography>
                      </Box>
                    </Box>
                  </motion.div>
                ))}
              </Box>

              {/* Message Input */}
              <Box sx={{ p: 2, borderTop: 1, borderColor: 'divider' }}>
                <Box sx={{ display: 'flex', gap: 1, alignItems: 'flex-end' }}>
                  <TextField
                    fullWidth
                    multiline
                    maxRows={3}
                    placeholder="Type your message..."
                    value={newMessage}
                    onChange={(e) => setNewMessage(e.target.value)}
                    onKeyPress={(e) => {
                      if (e.key === 'Enter' && !e.shiftKey) {
                        e.preventDefault();
                        handleSendMessage();
                      }
                    }}
                  />
                  <IconButton color="primary">
                    <AttachFile />
                  </IconButton>
                  <Button
                    variant="contained"
                    endIcon={<Send />}
                    onClick={handleSendMessage}
                    disabled={!newMessage.trim()}
                  >
                    Send
                  </Button>
                </Box>
              </Box>
            </Card>
          ) : (
            <Card sx={{ height: '100%', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
              <Box sx={{ textAlign: 'center' }}>
                <Typography variant="h6" color="text.secondary">
                  Select a conversation to start messaging
                </Typography>
              </Box>
            </Card>
          )}
        </Grid>
      </Grid>
    </Container>
  );
};

export default MessagesPage;
